import React from 'react'
import { useNavigate } from 'react-router-dom'
import { Popover } from 'react-bootstrap'
import { useRef } from 'react'
import { useEffect } from 'react'
import "bootstrap"
import {
  useWalletManager,
  useWallet,
  WalletConnectionStatus,
} from "@xiti/cosmodal"

import { Modal } from 'react-bootstrap'


function Navbar() {

  const { connect, disconnect } = useWalletManager()
  const { status, error, name, address, signingCosmWasmClient } = useWallet()

    let navigate = useNavigate()

    function toConnect() {
      navigate('/Connect')
  }

  var popover = new bootstrap.Popover(document.querySelector('#myButton'), options)

    const popoverRef = useRef()

    useEffect(() => {
      var popover = new bootstrap.Popover(popoverRef.current, {
          content: `${address}`,
          title: `${name}`
      })
    })



  // const popover = new Popover('.popover-dismiss', {
  //   trigger: 'focus'
  // })

    return (
        <div className='fixed-top'>
        <nav class="navbar navbar-expand-lg">
        <div class="container-fluid">
          <a class="navbar-brand" href="/">Legends of Hashish</a>
          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
            <div class="navbar-nav">
              <a class="nav-link" href="/">GlassFlow</a>
              <a class="nav-link" onClick={toConnect}>Connect</a>
              <a tabindex="0" class="btn btn-lg btn-danger" role="button" data-bs-toggle="popover" data-bs-trigger="focus" data-bs-title="Dismissible popover" data-bs-content="And here's some amazing content. It's very engaging. Right?">Dismissible popover</a>
            </div>
          </div>
        </div>
      </nav>
      </div>
    )
  }


export default Navbar
